import React, { useState } from 'react'
import axios from 'axios';
import {
  MDBBtn,
  MDBContainer,
  MDBRow,
  MDBCol,
  MDBCard,
  MDBCardBody,
  MDBInput,
  MDBIcon,
  MDBCheckbox
}
from 'mdb-react-ui-kit';


function App() {
  const [loginErr, setLoginErr] = useState(false);
  function handleSubmit(event) {
    event.preventDefault();
    const email = event.target.elements.email.value;
    const password = event.target.elements.password.value;
    const data = {
        "email": email,
        "password": password
    }
    const config = {
        headers: {
            'Content-Type': 'application/json'
        }
    }
    axios.post(URL, JSON.stringify(data), config)
        .then(response => {
            localStorage.setItem('token', response.data.token)
            localStorage.setItem('role', response.data.roleToken)
            window.location.reload();
        })
        .catch(error => {
            setLoginErr(true)

        });
}
  return (
    <MDBContainer fluid>
      {loginErr && (
                <div className='flex justify-start items-center' style={{ width: '380px', height: '100px', border: 'solid 3px var(--red-700)', borderRadius: '8px', marginBottom: '20px', transform:'200ms' }}>
                    <div className='w-3/12 flex '>
                        <Error />
                    </div>
                    <div className='flex flex-col justify-center items-start '>
                        <p className='text-red-600'>There is a problem</p>
                        <p>Your password is wrong</p>
                    </div>
                </div>
            )}
      <form ></form>
      <MDBRow className='d-flex justify-content-center align-items-center h-100'>
        <MDBCol col='12'>

          <MDBCard className='bg-white my-5 mx-auto' style={{borderRadius: '1rem', maxWidth: '500px'}}>
            <MDBCardBody className='p-5 w-100 d-flex flex-column'>

              <h2 className="fw-bold mb-2 text-center">Sign in</h2>
              <p className="text-white-50 mb-3">Please enter your login and password!</p>

              <MDBInput wrapperClass='mb-4 w-100' label='Email address' id='formControlLg' type='email' size="lg"/>
              <MDBInput wrapperClass='mb-4 w-100' label='Password' id='formControlLg' type='password' size="lg"/>

              <MDBCheckbox name='flexCheck' id='flexCheckDefault' className='mb-4' label='Remember password' />

              <MDBBtn size='lg'>
                Login
              </MDBBtn>

              <hr className="my-4" />

              <MDBBtn className="mb-6 w-100" size="lg" style={{backgroundColor: '#dd4b39'}}>
                <MDBIcon fab icon="google" className="mx-2"/>
                Sign in with google
              </MDBBtn>

            </MDBCardBody>
          </MDBCard>

        </MDBCol>
      </MDBRow>

    </MDBContainer>
  );
}

export default App;
